Player::Player() {

	hitpoints = 100;
	attackDamage = 50;

	//by default, the player object will face to the right
	playerFacing = Direction::RIGHT;
	sprite = PLAYER_SPRITE_RIGHT;
	
}

void Player::setNewDirectionFacing(Direction newDir) {
	playerFacing = newDir;
	
	switch (newDir) {

		case Direction::UP:
			sprite = PLAYER_SPRITE_UP;
			break;

		case Direction::DOWN:
			sprite = PLAYER_SPRITE_DOWN;
			break;

		case Direction::LEFT:
			sprite = PLAYER_SPRITE_LEFT;
			break;

		case Direction::RIGHT:
			sprite = PLAYER_SPRITE_RIGHT;
			break;

		default:
			sprite = PLAYER_SPRITE_RIGHT;

	}
}

uint32_t* Player::getSprite() {
	return sprite;
}

void Player::absorbDamage(int damage) {

}

Player::~Player() {

	delete[] sprite;

}
